args:
Add-Content -Path 'd:\algonode\static\css\style.css' -Value '
/* Modal styles */
.modal {
  display: none; 
  position: fixed; 
  z-index: 1000; 
  left: 0;
  top: 0;
  width: 100%; 
  height: 100%; 
  overflow: auto; 
  background-color: rgba(0,0,0,0.6); 
}

.modal-content {
  background-color: #333;
  margin: 10% auto; 
  padding: 20px;
  border: 1px solid #555;
  width: 80%; 
  max-width: 800px;
  color: #eee;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0,0,0,0.3);
}

.close {
  color: #aaa;
  float: right;
  font-size: 28px;
  font-weight: bold;
}

.close:hover,
.close:focus {
  color: #fff;
  text-decoration: none;
  cursor: pointer;
}

#runOutput {
  background: #111;
  padding: 15px;
  border-radius: 4px;
  white-space: pre-wrap;
  font-family: Consolas, monospace;
  max-height: 400px;
  overflow-y: auto;
  border: 1px solid #444;
}

.primary-btn {
  background-color: #4CAF50;
  color: white;
  border: none;
}
.primary-btn:hover {
  background-color: #45a049;
}
'
